# -*- coding: utf-8 -*-


import logging
from .base import BaseCommand

logger = logging.getLogger('cve-ease')

class StatusCommand(BaseCommand):
    """status"""
    parser = None
    @staticmethod
    def register(subparsers):
        StatusCommand.parser = subparsers.add_parser('status', help='show all status')
        StatusCommand.parser.add_argument('-v', '--verbose', dest='verbose', action='store_true', default=False,
                      help='show verbose output')


    def handle(self, options):
        """[basic] show all status"""

        if options.verbose:
            print("show all status")
        else:
            StatusCommand.parser.print_help()
