from .base import DBModel
from sqlalchemy import Column
from sqlalchemy import Text
from sqlalchemy import Integer
from sqlalchemy import Boolean




class ISSUE(DBModel):
    __tablename__ = 'GiteeIssue'

    id = Column('id', Integer, primary_key=True)

    affected_component = Column('affectedComponent', Text, index=True)
    html_url = Column('htmlUrl', Text)
    number = Column('number', Text, index=True)
    state = Column('state', Text)
    title = Column('title', Text, index=True)
    description = Column('description', Text)
    labels = Column('labels', Text)
    created_at = Column('createdAt', Text)
    finished_at = Column('finishedAt', Text)
    issue_type = Column('issueType', Text)
    issue_state = Column('issueState', Text)
    related_pr = Column('relatedPrs', Text)
