from .base import DBModel
from sqlalchemy import Column
from sqlalchemy import Text
from sqlalchemy import Integer
from sqlalchemy import Boolean




class PR(DBModel):
    __tablename__ = 'GiteePr'

    id = Column('id', Integer, primary_key=True)

    affected_component = Column('affectedComponent', Text, index=True)
    pr_number = Column('number', Text, index=True)
    pr_html_url = Column('prHtmlUrl', Text)
    pr_title = Column('prTitle', Text)
    close_related_issue = Column('closeRelatedIssue', Integer)
    created_at = Column('createdAt', Text)
    merged_at = Column('mergedAt', Text)
    pr_patch_url = Column('prPatchUrl', Text)
    pr_commit_url = Column('prCommitUrl', Text)
    pr_state = Column('prState', Text)
    pr_ref = Column('prRef', Text)
    commit_infos = Column('commitInfos', Text)
    related_issues = Column('relatedIssues', Text)


