# -*- coding: utf-8 -*-

from .base import DBModel
from sqlalchemy import Column
from sqlalchemy import Text
from sqlalchemy import Integer
from sqlalchemy import String


class NVD(DBModel):
    __tablename__ = 'NVD'
    id = Column('id', Integer, primary_key=True)

    cve_id = Column('cve_id', String(length=20), index=True)
    description = Column('description', Text)
    configurations = Column('configurations', Text)

    references = Column('references', Text)
    problemtype = Column('problemtype', Text)

    impact = Column('impact', Text)
    publishedDate = Column('publishedDate', String(length=20))
    lastModifiedDate = Column('lastModifiedDate', String(length=20))
