from .base import DBModel
from sqlalchemy import Column
from sqlalchemy import Text
from sqlalchemy import Integer
from sqlalchemy import Boolean
from sqlalchemy import Float




class RESULT(DBModel):
    __tablename__ = 'ScanResult'

    id = Column('id', Integer, primary_key=True)

    affectedComponent = Column('affectedComponent', Text, index=True)
    srpmFileName = Column('srpmFileName', Text)
    srpmFileLink = Column('srpmFileLink', Text)
    securityNoticeNo = Column('securityNoticeNo', Text)
    cveId = Column('cveId', Text, index=True)
    cnnvdId = Column('cnnvdId', Text, index=True)
    cnnvdTitle = Column('cnnvdTitle', Text)
    cvssScore = Column('cvssScore', Float)
    cvssVector = Column('cvssVector', Text)
    description = Column('description', Text)
    referenceLinks = Column('referenceLinks', Text)
    cweId = Column('cweId', Text)
    cweTitle = Column('cweTitle', Text)
    patchLink = Column('patchLink', Text)
    patchReferenceLink = Column('patchReferenceLink', Text)
    cveState = Column('cveState', Text)
    announcementTime = Column('announcementTime', Text)
    updateTime = Column('updateTime', Text)
    discoveryDate = Column('discoveryDate', Text)
    fixer = Column('fixer', Text, index=True)
    isFixed = Column('isFixed', Boolean, index=True)
    isISO = Column('isISO', Boolean, index=True)
    isSheet = Column('isSheet', Boolean, index=True)


