# -*- coding: utf-8 -*-


from .base import DBModel
from sqlalchemy import Column
from sqlalchemy import Text
from sqlalchemy import Integer
from sqlalchemy import Boolean


class RPM(DBModel):
    __tablename__ = 'RPM'
    id = Column('id', Integer, primary_key=True)

    # bin rpm split
    rpmname = Column('rpmname', Text)
    name = Column('name', Text)
    ver = Column('ver', Text)
    release = Column('release', Text)
    dist = Column('dist', Text)
    arch = Column('arch', Text)

    # src.rpm split
    sourcerpm = Column('sourcerpm', Text)
    sname = Column('sname', Text)
    sver = Column('sver', Text)
    srelease = Column('srelease', Text)
    sdist = Column('sdist', Text)

    # location like Packages/cmake-rpm-macros-3.16.5-4.oe1.noarch.rpm
    location = Column('location', Text)
    isISO = Column('isISO', Boolean)

    # pull path of rpm
    buildtime = Column('buildtime', Text)
    description = Column('description', Text)


class SRPM(DBModel):
    __tablename__ = 'SRPM'
    id = Column('id', Integer, primary_key=True)
    sourcerpm = Column('sourcerpm', Text)
    sname = Column('sname', Text)
    sver = Column('sver', Text)
    srelease = Column('srelease', Text)
    sdist = Column('sdist', Text)
    downloadurl = Column('downloadurl', Text)
    isISO = Column('isISO', Boolean)
    release_datetime = Column('releaseDatetime', Text) # 基于欧拉版本的commit时间



